pragma solidity >=0.6.0 <0.8.0;

import "./TrevToken.sol";

contract TrevTokenSale {
  address payable admin;
  TrevToken public tokenContract;
  uint256 public tokenPrice;
  uint256 public tokensSold;

  event Sell (
    address _buyer,
    uint256 _amount
  );

  constructor(TrevToken _tokenContract, uint256 _tokenPrice) public {
    admin = msg.sender;
    tokenContract = _tokenContract;
    tokenPrice = _tokenPrice;
  }

  function multiply(uint x, uint y) internal pure returns (uint z) {
    require(y == 0 || (z = x * y) / y == x);
  }

  function buyTokens(uint256 _numberOfTokens) public payable {
    require(msg.value == multiply(_numberOfTokens, tokenPrice));
    require(tokenContract.balanceOf(address(this)) >= _numberOfTokens);
    require(tokenContract.transfer(msg.sender, _numberOfTokens));

    tokensSold += _numberOfTokens;

    emit Sell(msg.sender, _numberOfTokens);
  }

  //Transfer amount of tokens in sale back to the admin and destroy contract
  function endSale() public {
    require(msg.sender == admin);
    require(tokenContract.transfer(admin, tokenContract.balanceOf(address(this))));


    //admin.transfer(address(this).balance);
    selfdestruct(admin);
  }
}
